import React from "react";
import PersonForm from "./PersonForm";

const Persons = ({ persons, deleteName, editName, finishEdit, handleEdit }) => {
    return (
        <div className="phonebook">
            {persons.map((person, Idx) => (
                <div>
                    <div className="phonebook-entry" key={Idx}>
                        <div>{person.name}</div>
                        {person.inEdit 
                            ? <>
                                <input type="text" defaultValue={person.number} onChange={handleEdit}/>
                              </>
                            : <>
                                <div>{person.number}</div>
                              </>
                        }
                        {person.inEdit 
                            ?  <button onClick={() => finishEdit(person)}>done</button>
                            :  <button onClick={() => editName(person)}>edit</button>
                        }

                        <button onClick={() => deleteName(person)}>delete</button>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Persons;
