import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import axios from 'axios'

import Filter from './components/Filter';
import Persons from './components/Persons'
import PersonForm from './components/PersonForm';

function App() {

  const persons = useRef([])
  const [ personsToDisplay, setPersonsToDisplay ] = useState([])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')


  useEffect(() => {
    axios
      .get('http://localhost:3001/api/persons')
      .then(response => {
        setPersonsToDisplay(response.data)
      })
  }, [])

  //grabs up to date info from database
  useEffect(() => {
      axios
      .get('http://localhost:3001/api/persons')
      .then(response => {
        persons.current = response.data
      })
  })

  const addName = (event) => {
    event.preventDefault()
    const nameObject = 
      {name: newName,
      number: newNumber}

      for (let person of persons.current) {
        console.log(person);
        if (person.name === newName) {
          if (window.confirm('Name already added, would you like to update number?')) {
            axios.put(`http://localhost:3001/api/persons/${person._id}`, nameObject)
              .then(response => {
                console.log(response);
                const updatedPerson = response.data
                const newPersons = persons.current.map(person => person._id === updatedPerson._id ? nameObject : person)
                setPersonsToDisplay(newPersons)
                console.log(newPersons);

              })
          }
          return
        }
      }
      //Adding new name to phonebook
      axios.post('http://localhost:3001/api/persons', nameObject)
      .then(response => {
        console.log(response)
        setPersonsToDisplay(persons.current.concat(nameObject))
      })
      .catch((error) => {
        console.log(error)
      })
  }

  const deleteName = (personToDelete) => {
    if (window.confirm(`Are you sure you want to delete ${personToDelete.name}`)) {
      console.log(personToDelete);
      axios.delete(`http://localhost:3001/api/persons/${personToDelete.name}`)
        .then(response => {
          const newPersonList = persons.current.filter(person => person._id !== personToDelete._id)
          setPersonsToDisplay(newPersonList)
          console.log(newPersonList);
        })
        .catch(error => {
          console.log('error in deletion');
        })
    }
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleNewSearch = (event) => {
    const searchName = event.target.value.toLowerCase();

    if (searchName === '') { 
      setPersonsToDisplay(persons.current)
      return;
    }
    const filteredPersons = persons.current.filter(person => person.name.toLowerCase().includes(searchName))
    setPersonsToDisplay(filteredPersons)
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <Filter handleNewSearch={handleNewSearch} />

      <PersonForm newName={newName} newNumber={newNumber}
                  handleNewName={handleNewName} handleNewNumber={handleNewNumber}
                  addName={addName} />

      <Persons persons={personsToDisplay} deleteName={deleteName}/>

    </div>
  );
}

export default App;
