import React from "react";
import PhoneInput from 'react-phone-number-input'
import plusIcon from '../plus.svg'


const PersonForm = ({newName, newNumber, handleNewName, handleNewNumber, addName}) => {

  let root = document.querySelector(':root');
  root.style.setProperty('--PhoneInputCountryFlag-height', '200px')
  return (
      <form className="personform">
        <div>
          <input placeholder="Enter Name" value={newName} onChange={handleNewName}/>
        </div>
        <div>
          <PhoneInput placeholder="Enter Number" value={newNumber} onChange={handleNewNumber} />
          {/* <input placeholder="Enter Number" value={newNumber} onChange={handleNewNumber}/> */}
        </div>
        <div>
          <button onClick={addName}><img src={plusIcon}></img></button>
        </div>
      </form>
    )
}

export default PersonForm;
