import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios'

import Filter from './components/Filter';
import Persons from './components/Persons'
import PersonForm from './components/PersonForm';

function App() {

  const [ persons, setPersons ] = useState([])
  const [ personsToDisplay, setPersonsToDisplay ] = useState([])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')

  useEffect(() => {
    axios
      .get('http://localhost:3001/api/persons')
      .then(response => {
        setPersons(response.data)
        setPersonsToDisplay(response.data)
      })
  }, [])

  const addName = (event) => {
    event.preventDefault()
    const nameObject = 
      {name: newName,
      number: newNumber}

      for (let person of persons) {
        if (person.name === newName) {
          window.alert('Name already added')
        }
        else {
          axios.post('http://localhost:3001/api/persons', nameObject)
            .then(response => {
              console.log(response)
              setPersons(persons.concat(nameObject))
              setPersonsToDisplay(persons.concat(nameObject))
            })
            .catch((error) => {
              console.log(error)
            })


        }
      }
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleNewSearch = (event) => {
    const searchName = event.target.value.toLowerCase();

    if (searchName === '') { 
      setPersonsToDisplay(persons)
      return;
    }
    const filteredPersons = persons.filter(person => person.name.toLowerCase().includes(searchName))
    setPersonsToDisplay(filteredPersons)
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <Filter handleNewSearch={handleNewSearch} />

      <PersonForm newName={newName} newNumber={newNumber}
                  handleNewName={handleNewName} handleNewNumber={handleNewNumber}
                  addName={addName} />

      <Persons persons={personsToDisplay} />

    </div>
  );
}

export default App;
